%this fucntion will calculate eigencentrality and return top 10 used ID and Max eigen values.function [UserID Ev]= eigencentrality(A)    [Up,LambdaP]=poweriteration(A);%using power iteration method  %[Up, LambdaP]= eigs(A,10);%using eigs functions  Ev= LambdaP;%Eigen value  disp("maximum Eigen Value:");  disp(Ev);  %sorting to calculate top 10 maximum centrality  sortvalues=sort(Up,'ascend');  maxValues = sortvalues(end-10:end);%taking top 10  maxIndex = ismember(Up, maxValues);%Useer ID of top 10  UserID= find(maxIndex);  %printing high eigen centrality user ID.  disp("Top 10 high Eigen centrality user:");  disp("UserID:")  disp(UserID')  endfunction
